import React, { useState } from 'react';
import JobTrendingChart from './JobTrendingChart';

export default function App() {
  const [description, setDescription] = useState('');
  const [results, setResults] = useState([]);
  const [jobTypes, setJobTypes] = useState([]);
  const [filter, setFilter] = useState('All');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  async function handleSearch(e) {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setResults([]);
    setJobTypes([]);

    try {
      const res = await fetch('http://localhost:5003/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ description }),
      });
      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Error pencarian');

      if (data.message) setMessage(data.message);
      if (data.results) setResults(data.results);
      if (data.job_types) setJobTypes(data.job_types);
    } catch (err) {
      setMessage(err.message);
    } finally {
      setLoading(false);
    }
  }

  const filteredResults = filter === 'All' ? results : results.filter(r => r.job_type === filter);

  return (
    <div style={{ maxWidth: 900, margin: '40px auto', fontFamily: 'Poppins, sans-serif', padding: '20px', backgroundColor: '#fce4ec', borderRadius: '12px' }}>
      <h1 style={{ color: '#ec407a', textAlign: 'center', marginBottom: '30px' }}>KARSO - Cari Kerja</h1>

      {/* Form pencarian */}
      <form onSubmit={handleSearch} style={{ marginBottom: '40px', display: 'flex', flexDirection: 'column', gap: '15px' }}>
        <textarea
          rows={3}
          placeholder="Contoh: UI designer, kerja remote, startup"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
          style={{
            width: '100%',
            padding: '15px',
            fontSize: '16px',
            borderRadius: '8px',
            border: '1px solid #ddd',
            resize: 'vertical',
            boxSizing: 'border-box',
            fontFamily: 'Poppins, sans-serif'
          }}
        />
        <button
          type="submit"
          disabled={loading}
          style={{
            padding: '12px 40px',
            borderRadius: '30px',
            backgroundColor: '#ec407a',
            color: 'white',
            border: 'none',
            fontWeight: '700',
            fontSize: '16px',
            cursor: loading ? 'not-allowed' : 'pointer',
            alignSelf: 'flex-start',
            transition: 'background-color 0.3s ease',
          }}
          onMouseEnter={e => !loading && (e.target.style.backgroundColor = '#d81b60')}
          onMouseLeave={e => !loading && (e.target.style.backgroundColor = '#ec407a')}
        >
          {loading ? 'Mencari...' : 'üîç MENCARI'}
        </button>
      </form>

      {/* Pesan error atau info */}
      {message && <p style={{ color: '#c2185b', textAlign: 'center', marginBottom: '30px', fontWeight: '600' }}>{message}</p>}

      {/* Filter jenis kerja */}
      {jobTypes.length > 0 && (
        <div style={{ marginBottom: '30px', textAlign: 'center' }}>
          <label htmlFor="filter" style={{ fontWeight: '700', marginRight: '10px', fontSize: '16px' }}>Filter Jenis Kerja:</label>
          <select
            id="filter"
            value={filter}
            onChange={e => setFilter(e.target.value)}
            style={{
              padding: '8px 12px',
              fontSize: '16px',
              borderRadius: '8px',
              border: '1px solid #ddd',
              minWidth: '180px',
              cursor: 'pointer'
            }}
          >
            <option value="All">Semua</option>
            {jobTypes.map((jt, i) => (
              <option key={i} value={jt}>{jt}</option>
            ))}
          </select>
        </div>
      )}

      {/* Daftar hasil pekerjaan */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(280px,1fr))', gap: '20px', marginBottom: '50px' }}>
        {filteredResults.map((job, i) => (
          <div
            key={i}
            style={{
              backgroundColor: '#fff',
              borderRadius: '15px',
              padding: '20px',
              boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
              display: 'flex',
              flexDirection: 'column',
              gap: '8px',
              transition: 'transform 0.2s',
            }}
            onMouseEnter={e => (e.currentTarget.style.transform = 'scale(1.03)')}
            onMouseLeave={e => (e.currentTarget.style.transform = 'scale(1)')}
          >
            <h3 style={{ color: '#ec407a', marginBottom: '8px' }}>{job.job_title}</h3>
            <p><strong>Perusahaan:</strong> {job.company_name}</p>
            <p><strong>Lokasi:</strong> {job.company_location}</p>
            <p><strong>Email:</strong> {job.company_email}</p>
            <p><strong>Skor Kecocokan:</strong> {(job.score * 100).toFixed(2)}%</p>
          </div>
        ))}
      </div>

      {/* Grafik tren pekerjaan direkomendasikan */}
      <div style={{ marginTop: '40px' }}>
        <JobTrendingChart />
      </div>
    </div>
  );
}
