import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './App.css';

export default function ResultPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const description = location.state?.description || '';

  const [results, setResults] = useState([]);
  const [jobTypes, setJobTypes] = useState([]);
  const [filter, setFilter] = useState('All');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!description) {
      navigate('/');
      return;
    }

    async function fetchResults() {
      setLoading(true);
      setMessage('');
      setResults([]);
      setJobTypes([]);

      try {
        const res = await fetch('http://localhost:5003/api/search', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ description }),
        });
        const data = await res.json();

        if (!res.ok) throw new Error(data.error || 'Error pencarian');

        if (data.message) setMessage(data.message);
        if (data.results) setResults(data.results);
        if (data.job_types) setJobTypes(data.job_types);
      } catch (err) {
        setMessage(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchResults();
  }, [description, navigate]);

  const filteredResults = filter === 'All' ? results : results.filter(r => r.job_type === filter);

  return (
    <div className="app-container result-container">
      <h2 style={{ textAlign: 'center', marginBottom: '10px' }}>Hasil Rekomendasi Pekerjaan</h2>
      <p style={{ textAlign: 'center', marginBottom: '30px', color: '#555' }}>
        Berikut daftar pekerjaan yang cocok untukmu
      </p>

      <button
        onClick={() => navigate('/')}
        className="btn-back"
        style={{ marginBottom: 20 }}
      >
        ðŸ”™ Kembali
      </button>

      {loading && <p style={{ textAlign: 'center' }}>Loading...</p>}
      {message && <p className="form-message">{message}</p>}

      {jobTypes.length > 0 && (
        <div className="filter-container" style={{ marginBottom: 20, textAlign: 'right' }}>
          <label htmlFor="filter">Filter Jenis Kerja:</label>
          <select
            id="filter"
            value={filter}
            onChange={e => setFilter(e.target.value)}
            style={{ marginLeft: 10, padding: '6px 10px', borderRadius: 6, border: '1px solid #ddd', cursor: 'pointer' }}
          >
            <option value="All">Semua</option>
            {jobTypes.map((jt, i) => (
              <option key={i} value={jt}>{jt}</option>
            ))}
          </select>
        </div>
      )}

      <div className="job-list">
        {filteredResults.length > 0 ? (
          filteredResults.map((job, i) => (
            <div key={i} className="job-card">
              <h3
                onClick={() => navigate('/job-detail', { state: { job } })}
                style={{ color: '#ec407a', cursor: 'pointer', textDecoration: 'underline' }}
                title="Klik untuk lihat detail pekerjaan"
              >
                {job.job_title}
              </h3>
              <p><strong>Perusahaan:</strong> {job.company_name}</p>
              <p><strong>Email:</strong> {job.company_email}</p>
              <p><strong>Lokasi:</strong> {job.company_location}</p>

              <p><strong>Skor Kecocokan</strong></p>
              <progress
                value={job.score}
                max="1"
                className="score-progress"
                aria-label={`Skor kecocokan ${job.score}`}
              />
              <small className="score-text">{job.score.toFixed(4)}</small>
            </div>
          ))
        ) : (
          !loading && <p style={{ textAlign: 'center' }}>Tidak ada hasil pencarian.</p>
        )}
      </div>
    </div>
  );
}
