import React, { useState, useRef } from 'react';
import JobTrendingChart from './JobTrendingChart';
import './App.css';

export default function App() {
  const [description, setDescription] = useState('');
  const [results, setResults] = useState([]);
  const [jobTypes, setJobTypes] = useState([]);
  const [filter, setFilter] = useState('All');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Ref untuk scroll ke form
  const formRef = useRef(null);

  // Fungsi scroll halus ke form
  const scrollToForm = () => {
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  async function handleSearch(e) {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setResults([]);
    setJobTypes([]);

    try {
      const res = await fetch('http://localhost:5003/api/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ description }),
      });

      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Error pencarian');

      if (data.message) setMessage(data.message);
      if (data.results) setResults(data.results);
      if (data.job_types) setJobTypes(data.job_types);
    } catch (err) {
      setMessage(err.message);
    } finally {
      setLoading(false);
    }
  }

  const filteredResults = filter === 'All' ? results : results.filter(r => r.job_type === filter);

  return (
    <div>
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-text">
          <img src="/logo.png" alt="Logo KARSO" className="hero-logo" />
          <h1>Kami Sedang Mencari!</h1>
          <p>Ayo gabung bersama ribuan pencari kerja lainnya dan temukan peluang terbaik untuk masa depan kariermu bersama KARSO.</p>
          <button className="btn-hero" onClick={scrollToForm}>Mulai Cari Kerja</button>
        </div>
        <div className="hero-image-container">
          <img src="/hero-illustration.png" alt="Ilustrasi Karir" className="hero-image" />
        </div>
      </section>

      {/* Form Section */}
      <section className="form-section" ref={formRef}>
        <div className="form-container">
          <h2>Cari Pekerjaan Impianmu üíº</h2>
          <p>Masukkan deskripsi pekerjaan yang kamu inginkan</p>
          <form onSubmit={handleSearch}>
            <label htmlFor="description" className="form-label">Deskripsi pekerjaan</label>
            <textarea
              id="description"
              rows={3}
              placeholder="Contoh: UI designer, kerja remote, startup"
              value={description}
              onChange={e => setDescription(e.target.value)}
              required
            />
            <button type="submit" disabled={loading} className="btn-search">
              {loading ? 'Mencari...' : 'üîç MENCARI'}
            </button>
          </form>

          {message && <p className="form-message">{message}</p>}

          {jobTypes.length > 0 && (
            <div className="filter-container">
              <label htmlFor="filter">Filter Jenis Kerja:</label>
              <select id="filter" value={filter} onChange={e => setFilter(e.target.value)}>
                <option value="All">Semua</option>
                {jobTypes.map((jt, i) => (
                  <option key={i} value={jt}>{jt}</option>
                ))}
              </select>
            </div>
          )}

          <div className="job-list">
            {filteredResults.map((job, i) => (
              <div key={i} className="job-card">
                <h3>{job.job_title}</h3>
                <p><strong>Perusahaan:</strong> {job.company_name}</p>
                <p><strong>Lokasi:</strong> {job.company_location}</p>
                <p><strong>Email:</strong> {job.company_email}</p>
                <p><strong>Skor Kecocokan:</strong> {(job.score * 100).toFixed(2)}%</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Trending Chart Section */}
      <section className="chart-section">
        <JobTrendingChart />
      </section>
    </div>
  );
}
